/** @jsx jsx */

import { jsx, css } from '@emotion/core';
import { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firebaseConnect } from 'react-redux-firebase';
import Toggle from 'react-toggle';
import 'react-toggle/style.css';

import Checkboxes from 'components/Checkboxes';
import Header from 'components/Header';
import Input from 'components/Input';
import PicUpload from 'components/PicUpload';
import Select from 'components/Select';
import Textarea from 'components/Textarea';

const pageProfileLayoutStyle = css`
  .profile-container {
    margin-top: 10px;
  }

  .profile-pic {
    float: left;
    margin-right: 10px;
  }

  .profile-input {
    text-align: left;
    width: 300px;
    height: 43px;
    margin-bottom: 9px;
    color: #545353;
    background: #f4f2f2;
    border: 0px;
  }

  .profile-select {
    background: #f4f2f2;
    background-image: url(https://image.flaticon.com/icons/svg/60/60781.svg);
    background-position: 95% 50%;
    background-repeat: no-repeat;
    background-size: 16px;
    border: 0px;
    width: 300px;
    height: 43px;
    margin-bottom: 9px;
    color: #545353;
  }

  .choice-container {
    margin-bottom: 5px;
    display: inline-block;
  }

  .side-description {
    width: 210px;
    display: inline-block;
    vertical-align: top;
  }

  .bottom-elements {
    margin-top: 15px;
  }

  .profile-text-area {
    text-align: left;
    width: 300px;
    height: 75px;
    color: #545353;
    background: #f4f2f2;
    border: 0px;
  }

  .text-area-container {
    display: inline-block;
  }

  .save-button {
    margin-left: 210px;
    width: 300px;
  }

  .incomplete-message {
    margin-top: 5px;
    margin-left: 210px;
    color: red;
  }

  .react-toggle--checked .react-toggle-track {
    background-color: #bd574e;
  }

  .react-toggle--checked:hover:not(.react-toggle--disabled)
    .react-toggle-track {
    background-color: #6c2c2c;
  }

  .react-toggle--checked .react-toggle-thumb {
    border-color: #bd574e;
  }
`;

class PageProfile extends Component {
  constructor(props) {
    super(props);
    const {
      description,
      dorm,
      email,
      gender,
      lookingFor,
      lookingForGender,
      name,
      show,
      year,
    } = props.profile;

    this.state = {
      description,
      dorm,
      email,
      gender,
      lookingFor,
      lookingForGender,
      name,
      show,
      year,
    };
  }

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleCheckboxChange = (name, response) => {
    const values = this.state[name];
    const bool = values ? !values[response] : true;
    const newValues = {
      ...values,
      [response]: bool,
    };
    this.setState({ [name]: newValues });
  };

  handleRadioChange = (name, response) => {
    this.setState({ [name]: response });
  };

  handleToggleChange = event => {
    this.setState({ show: !this.state.show });
  };

  checkRequired = () => {
    const required = ['dorm', 'gender', 'name', 'year'];

    let completed = required.every(requirement => this.state[requirement]);

    const { lookingForGender } = this.state;
    const { Male, Female, Nonbinary } = lookingForGender || {};
    const lookingFor = this.state.lookingFor || {};

    // Using bracket notation as 'true love' is not a valid identifier
    const truelove = lookingFor['true love'];
    const bestie = lookingFor['bestie'];

    completed =
      completed && (Male || Female || Nonbinary) && (truelove || bestie);

    return completed;
  };

  checkChanged = () => {
    const { profile } = this.props;
    const { Male, Female, Nonbinary } = this.state.lookingForGender || {};
    const lookingForGender = profile.lookingForGender || {};
    const lookingFor = profile.lookingFor || {};
    const lookingForState = this.state.lookingFor || {};

    // Using bracket notation as 'true love' is not a valid identifier
    const truelove = lookingForState['true love'];
    const bestie = lookingForState['bestie'];

    const needCheck = ['description', 'dorm', 'gender', 'name', 'show', 'year'];

    let same = needCheck.every(input => this.state[input] === profile[input]);

    // ^ is XOR. We need it to deal with undefined === false cases.
    same =
      same &&
      !(Male ^ lookingForGender['Male']) &&
      !(Female ^ lookingForGender['Female']) &&
      !(Nonbinary ^ lookingForGender['Nonbinary']) &&
      !(truelove ^ lookingFor['true love']) &&
      !(bestie ^ lookingFor['bestie']);

    return same;
  };

  saveProfile = () => {
    if (this.checkRequired()) {
      const lowerCaseName = this.state.name.toLowerCase();
      const { show } = this.state;
      this.props.updateProfile({
        ...this.state,
        last: show ? lowerCaseName.split(' ').slice(-1)[0] : null,
        searchName: show ? lowerCaseName : null,
      });
      alert('Info saved!');
    }
  };

  render() {
    const { email, profile_pic, updateProfile } = this.props;

    var filled = this.checkRequired();
    var changed = this.checkChanged();
    return (
      <div className="PageProfile" css={pageProfileLayoutStyle}>
        <Header>PROFILE</Header>
        <br />
        <div className="profile-container">
          <div>
            <div className="profile-pic">
              <PicUpload
                name={email}
                original_pic={profile_pic}
                path="/profile_pics"
                size={200}
                updateURL={updateProfile}
              />
            </div>
            <Input
              className="profile-input"
              handleInputChange={this.handleInputChange}
              name="name"
              placeholder="Your name"
              type="text"
              value={this.state.name}
            />
            <Select
              className="profile-select"
              handleInputChange={this.handleInputChange}
              name="dorm"
              placeholder="Dorm"
              value={this.state.dorm}
              values={this.props.dorms}
            />
            <Select
              className="profile-select"
              handleInputChange={this.handleInputChange}
              name="year"
              placeholder="Year"
              value={this.state.year}
              values={[
                'First Year',
                'Sophomore',
                'Junior',
                'Senior',
                'Grad student',
              ]}
            />
            <Select
              className="profile-select"
              handleInputChange={this.handleInputChange}
              name="gender"
              placeholder="Gender"
              value={this.state.gender}
              values={['Male', 'Female', 'Nonbinary']}
            />
            <div className="bottom-elements">
              <div className="side-description">LOOKING FOR...</div>
              <div className="choice-container">
                <Checkboxes
                  handleClick={this.handleCheckboxChange}
                  name="lookingForGender"
                  responses={['Male', 'Female', 'Nonbinary']}
                  values={this.state.lookingForGender}
                />
              </div>
            </div>
            <div className="bottom-elements">
              <div className="side-description">TO BE MY...</div>
              <div className="choice-container">
                <Checkboxes
                  handleClick={this.handleCheckboxChange}
                  name="lookingFor"
                  values={this.state.lookingFor}
                  responses={['true love', 'bestie']}
                />
              </div>
            </div>
            <div className="bottom-elements">
              <div className="side-description">PERSONAL INFO...</div>
              <div className="text-area-container">
                <Textarea
                  className="profile-text-area"
                  handleInputChange={this.handleInputChange}
                  name="description"
                  placeholder="Write a bit about yourself!"
                  rows={2}
                  type="text"
                  value={this.state.description}
                />
              </div>
            </div>
            <div className="bottom-elements">
              <div className="side-description">SHOW IN SEARCH...</div>
              <Toggle
                defaultChecked={this.state.show}
                onChange={this.handleToggleChange}
              />
            </div>
          </div>
          <br />
          <div className="save-button">
            <button
              onClick={this.saveProfile}
              style={{
                opacity: filled ? 1 : 0.8,
                margin: '0 auto',
              }}
            >
              Save Changes
            </button>
          </div>
          {!filled && (
            <div className="incomplete-message">
              Please fully complete profile before saving.
            </div>
          )}
          {!changed && (
            <div className="incomplete-message">You have unsaved changes.</div>
          )}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  return {
    email: state.firebase.auth.email,
    profile: state.firebase.profile,
    profile_pic: state.firebase.profile.profile_pic,
    updateProfile: props.firebase.updateProfile,
  };
};

export default compose(
  firebaseConnect(),
  connect(mapStateToProps),
)(PageProfile);
