import * as React from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { Route, Switch, Redirect } from 'react-router-dom';
import { firebaseConnect } from 'react-redux-firebase';

import Loading from 'components/Loading';
import { PrivateRoute, PublicRoute } from 'components/PrivateRoute';

import PageApp from 'pages/PageApp';
import PageAuth from 'pages/PageAuth';
import PageMain from 'pages/PageMain';
import RestaurantPortal from 'pages/RestaurantPortal';
import SchoolPortal from 'pages/SchoolPortal';

class RootImpl extends React.Component {
  render() {
    const { emailVerified, isLoaded } = this.props;

    if (!isLoaded) {
      return (
        <div style={{ margin: '10% auto' }}>
          <Loading size={200} type="spin" />
        </div>
      );
    }

    return (
      <div className="Root">
        <Switch>
          <PrivateRoute
            path="/app"
            component={emailVerified ? PageApp : PageAuth}
          />
          <PublicRoute exact path="/" component={PageMain} />
          <Route
            exact
            path="/(auth|login|register|reset)/"
            component={PageAuth}
          />
          <Route path="/school_portal" component={SchoolPortal} />
          <Route path="/restaurant" component={RestaurantPortal} />
          <Route path="/archives" component={PageArchives} />
          <Route component={() => <Redirect to="/" />} />
        </Switch>
      </div>
    );
  }
}

const mapStateToProps = state => {
  const { auth, profile } = state.firebase;

  return {
    emailVerified: auth.emailVerified,
    isLoaded: auth.isLoaded && profile.isLoaded,
  };
};

export default compose(firebaseConnect(), connect(mapStateToProps))(RootImpl);
