import React, { Component } from 'react';

// import Input from './components/Input';
// import RowToggle from './components/RowToggle';
import PasswordReset from './components/PasswordReset';
import Modal from 'react-bootstrap/Modal';

export default class PageSettingsLayout extends Component {
  constructor(props) {
    super(props);

    this.state = {
      show: false,
      password: '',
    };
  }

  handleInputChange = event => {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <div className="PageSettings">
        <div className="header">SETTINGS</div>
        <br />
        {/* <Input text="email" name="email" type="email" value={this.state.email}/> */}
        <div className="Box">
          <PasswordReset
            email={this.props.email}
            auth={this.props.auth}
            resetPassword={this.props.resetPassword}
            reauthenticate={this.props.reauthenticate}
          />
        </div>
        <br />
        <Modal
          show={this.state.show}
          onHide={() => {
            this.setState({ show: false });
          }}
        >
          <Modal.Body style={{ textAlign: 'center', padding: 40 }}>
            <div style={{ fontSize: 22 }}>
              Are you sure you want to delete your entire account?{' '}
              <b>There's no going back!</b>
            </div>
            <br />
            <br />
            {/*<Input
              name="password"
              placeholder="confirm your password to delete"
              type="password"
              value={this.state.password}
              handleInputChange={this.handleInputChange}
            />
            <br />*/}
            <button
              onClick={() => {
                this.props.deleteUser();
                alert('Account Deleted :(');
                // this.props.reauthenticate(this.state.password)
                //   .then(data => {
                //     this.props.deleteUser();
                //     alert('Account Deleted :(')
                //   })
                //   .catch(error => {
                //     alert('Incorrect password')
                //     this.setState({password: ""})
                //   })
              }}
            >
              I'm sure. Delete my account.
            </button>
            <br />
            <br />
            <div
              onClick={() => this.setState({ show: false, password: '' })}
              style={{ cursor: 'pointer' }}
            >
              Cancel
            </div>
          </Modal.Body>
        </Modal>

        <div className="Box">
          <h5>Delete Account</h5>
          <br />
          <button onClick={() => this.setState({ show: true })}>
            Delete Your Account
          </button>
        </div>
        <br />
      </div>
    );
  }
}
