/** @jsx jsx **/

import { Component } from 'react';
import { jsx, css } from '@emotion/core';

import Header from 'components/Header';

const checkInStyle = css`
  font-weight: bold;

  .conf-container {
    margin: 20px auto;
  }

  button {
    max-width: 300px;
    margin-right: 0.5em;
    padding: 0.1em;
  }

  input {
    max-width: 359px;
    height: 40px;
    border: none;
    background: #f4f2f2;
    font-family: 'Apercu-light';
    display: flex;
    align-items: center;
    letter-spacing: 0.05em;

    color: #545353;
  }
`;

const dummySchedule = [
  {
    date: '2/14 (Sat)',
    allotted: 75,
    claimed: 30,
  },
  {
    date: '2/15 (Sun)',
    allotted: 60,
    claimed: 30,
  },
  {
    date: '2/16 (Mon)',
    allotted: 35,
    claimed: 20,
  },
]

export default class RestaurantHome extends Component {

  constructor(props) {
    super(props);

    this.state = {
      code: '',
      display: false,
      errorMessage: '',
      valid: null,
    };
  }

  renderDateSchedule() {
    const dateSchedule = this.props.dateSchedule || dummySchedule;
    return (
      <div>
        <p><b>Date schedule:</b></p>
        <table>
          <tr>
            <th><b>Date</b></th>
            <th><b>Claimed</b></th>
            <th><b>Allotted</b></th>
          </tr>
          {dateSchedule.map(date => 
            <tr>
              <td>{date.date}</td>
              <td>{date.claimed}</td>
              <td>{date.allotted}</td>
            </tr>
          )}
          <tr>
              <td>Total</td>
              <td>{dateSchedule.reduce((total, num) => total + num.claimed, 0)}</td>
              <td>{dateSchedule.reduce((total, num) => total + num.allotted, 0)}</td>
          </tr>
        </table>
      </div>
    );
  }

  renderButtons() {
    return (
      <div>
        <a href='/restaurant/check-in'><button>CHECK-IN ONLINE</button></a>
        <button>PRINT CONFIRMATION CODES</button>
      </div>
    )
  }

  renderInfo() {
    return (
      <div>
        <p>
          <b>Date description: </b>
          {this.props.dateDescription || <div>Placeholder description.</div>}
        </p>
        <p>
          <b>Contract description: </b>
          {this.props.contractDescription || <div>Contact <a href='mailto:cupids@datamatch.me'>cupids@datamatch.me</a> for the contact description.</div>}
        </p>
        <br />
        {this.renderDateSchedule()}
      </div>
    )
  }

  render() {
    return (
      <div className="RestaurantHome" css={checkInStyle}>
        <Header>RESTAURANT PORTAL</Header>
        <p>
          <b>Welcome to the restaurant portal!</b>
          <br />
          You can print confirmation codes below, or see the agreement you negotiated with the Datamatch team in more detail.
          <br />
          Contact us at  if youâ€™d like to make changes.
        </p>

        {this.renderButtons()}
        {this.renderDateInfo()}
      </div>
    );
  }
}
