import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firebaseConnect, firestoreConnect } from 'react-redux-firebase';
import { Route, Switch, Redirect } from 'react-router-dom';

import Sidebar from 'components/Sidebar';
import NavigationBar from './NavigationBar';
import Home from './Home';
import Profile from './Profile';
import Team from './Team';

import Loading from 'components/Loading';
import PageLogin from 'components/Login';

class SchoolPortal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      school: props.profile.schoolAdmin,
    };
  }

  async componentDidMount() {
    const { firebase, profile } = this.props;
    let school = profile.schoolAdmin;

    // if we aren't 100% sure this person is a schoolAdmin
    if (!school) {
      const { data } = await firebase
        .functions()
        .httpsCallable('user-makeSchoolAdmin')();
      school = data;
    }

    this.setState({ loading: false, school });
  }

  render() {
    if (this.state.loading) {
      return (
        <div style={{ height: 200 }}>
          <Loading />
        </div>
      );
    }

    const { isLoggedIn, loginUser } = this.props;
    const { school } = this.state;

    if (!isLoggedIn) {
      return <PageLogin loginUser={loginUser} title="University Portal" />;
    }

    if (!school) {
      return <Redirect to="/" />;
    }

    return (
      <div className="PageSchool">
        <Sidebar leftSidebar={<NavigationBar />}>
          <Switch>
            <Route exact path="/school_portal" component={Home} />
            <Route
              exact
              path="/school_portal/school"
              render={() => <Profile school={school} />}
            />
            <Route
              exact
              path="/school_portal/survey"
              render={() => <h1>yelloooo</h1>}
            />
            <Route
              exact
              path="/school_portal/team"
              render={() => <Team school={school} />}
            />
            <Route component={() => <Redirect to="/school_portal" />} />
          </Switch>
        </Sidebar>
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  return {
    isLoggedIn: state.firebase.auth.uid ? true : false,
    loginUser: props.firebase.login,
    profile: state.firebase.profile,
  };
};

export default compose(
  firebaseConnect(),
  firestoreConnect(),
  connect(mapStateToProps),
)(SchoolPortal);
