import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { bindActionCreators } from 'redux';
import {
  firestoreConnect,
  firebaseConnect,
  isLoaded,
} from 'react-redux-firebase';
import { Route, Switch, Redirect } from 'react-router-dom';

import Sidebar from 'components/Sidebar';
import NavigationBar from './NavigationBar.js';

import Loading from 'components/Loading';
import PageLogin from 'components/Login';

class SchoolPortal extends Component {
  render() {
    const { email, isLoggedIn, loginUser, restaurantAdmins } = this.props;

    console.log(restaurantAdmins);

    if (!isLoaded(this.props.restaurantAdmins)) {
      return (
        <div style={{ height: 200 }}>
          <Loading />
        </div>
      );
    }

    const restaurant = restaurantAdmins.admins_to_restaurant[email];

    if (!isLoggedIn) {
      return <PageLogin loginUser={loginUser} title="Restaurant Portal" />;
    }
    if (!restaurant) {
      return <Redirect to="/" />;
    }

    return (
      <div className="PageRestaurant">
        <Sidebar leftSidebar={<NavigationBar admin={this.props.admin} />}>
          <Switch>
            <Route
              exact
              path="/restaurant"
              render={() => {
                return <h1>You are in!</h1>;
              }}
            />
            <Route
              exact
              path="/restaurant/check-in"
              render={() => {
                return <h1>Checking in?</h1>;
              }}
            />
            <Route component={() => <Redirect to="/restaurant" />} />
          </Switch>
        </Sidebar>
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  return {
    loginUser: props.firebase.login,
    isLoggedIn: state.firebase.auth.uid ? true : false,
    email: state.firebase.auth.email,
    restaurantAdmins: state.firestore.data.restaurant_admins,
  };
};

function mapDispatchToProps(dispatch) {
  return bindActionCreators({}, dispatch);
}

export default compose(
  firebaseConnect(),
  firestoreConnect(() => [
    {
      collection: 'portals',
      doc: 'restaurant_admins',
      storeAs: 'restaurant_admins',
    },
  ]),
  connect(mapStateToProps, mapDispatchToProps),
)(SchoolPortal);
