import React, { Component } from 'react';
import { deepCompare } from 'utils/helpers';
import Input from '../components/Input';

export default class PageAdminConfig extends React.Component {
  state = {
    settings: this.props.settings,
    propSettings: this.props.settings,
    college: '',
    email: '',
  };

  static getDerivedStateFromProps(props, state) {
    let equal = deepCompare(state.propSettings, props.settings);

    if (!equal) {
      return {
        ...state,
        propSettings: props.settings,
        settings: props.settings,
      };
    }
    return null;
  }

  render() {
    var equal = deepCompare(this.state.settings, this.props.settings);
    let colleges = [...this.state.settings.colleges];
    return (
      <div className="PageAdminConfig">
        <div className="Box">
          <h3>Colleges</h3>
          {colleges.map(college => {
            return (
              <div
                style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                }}
              >
                <h5>{college}</h5>
                <button
                  style={{ width: 40, background: 'white', color: 'black' }}
                  onClick={() => {
                    this.setState({
                      ...this.state,
                      settings: {
                        ...this.state.settings,
                        colleges: colleges.filter(item => item !== college),
                      },
                    });
                  }}
                >
                  X
                </button>
              </div>
            );
          })}
          <form
            onSubmit={event => {
              event.preventDefault();
              if (this.state.college.length > 0) {
                this.setState({
                  ...this.state,
                  college: '',
                  settings: {
                    ...this.state.settings,
                    colleges: [...colleges, this.state.college],
                  },
                });
              }
            }}
          >
            <div
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
              }}
            >
              <input
                type="text"
                placeholder="Enter a college"
                value={this.state.college}
                onChange={event =>
                  this.setState({ college: event.target.value })
                }
              />
              <button>add college</button>
            </div>
          </form>
        </div>

        <div className="Box">
          <h3>Email to College</h3>
          {Object.keys(this.state.settings.email_to_college).map(email => {
            let college = this.state.settings.email_to_college[email];
            let email_to_college = { ...this.state.settings.email_to_college };

            return (
              <div style={{ marginBottom: 15 }}>
                <div
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                  }}
                >
                  <div>{email}</div>
                  <button
                    style={{ width: 40, background: 'white', color: 'black' }}
                    onClick={() => {
                      delete email_to_college[email];
                      this.setState({
                        ...this.state,
                        settings: {
                          ...this.state.settings,
                          email_to_college: {
                            ...email_to_college,
                          },
                        },
                      });
                    }}
                  >
                    X
                  </button>
                </div>
                <select
                  value={college || null}
                  onChange={event => {
                    this.setState({
                      settings: {
                        ...this.state.settings,
                        email_to_college: {
                          ...this.state.settings.email_to_college,
                          [email]: event.target.value,
                        },
                      },
                    });
                  }}
                >
                  {[...this.state.settings.colleges, null].map(college => {
                    return (
                      <option key={college} value={college}>
                        {college}
                      </option>
                    );
                  })}
                </select>
              </div>
            );
          })}
          <br />
          <br />
          <form
            onSubmit={event => {
              event.preventDefault();
              if (this.state.email.length > 1) {
                this.setState({
                  ...this.state,
                  email: '',
                  settings: {
                    ...this.state.settings,
                    email_to_college: {
                      ...this.state.settings.email_to_college,
                      [this.state.email]: colleges[0],
                    },
                  },
                });
              }
            }}
          >
            <div
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
              }}
            >
              <input
                type="text"
                placeholder="Enter an email type"
                value={this.state.email}
                onChange={event => this.setState({ email: event.target.value })}
              />
              <button>add email</button>
            </div>
          </form>
        </div>

        <div className="Box">
          <h3>College Dorms</h3>
          {this.state.settings.colleges.map(college => {
            let dorms = this.state.settings.college_to_dorm[college];
            return (
              <div>
                <h4>{college}</h4>
                <div>
                  {dorms &&
                    dorms.map(dorm => {
                      return (
                        <div
                          style={{
                            display: 'inline-block',
                            padding: 5,
                            background: 'grey',
                            margin: 5,
                          }}
                        >
                          <div style={{ display: 'inline-block' }}>{dorm}</div>
                          <button
                            style={{
                              width: 30,
                              height: 30,
                              background: 'grey',
                              color: 'black',
                              display: 'inline-block',
                            }}
                            onClick={() => {
                              this.setState({
                                ...this.state,
                                settings: {
                                  ...this.state.settings,
                                  college_to_dorm: {
                                    ...this.state.settings.college_to_dorm,
                                    [college]: dorms.filter(
                                      item => item !== dorm,
                                    ),
                                  },
                                },
                              });
                            }}
                          >
                            X
                          </button>
                        </div>
                      );
                    })}
                </div>
                <Input
                  placeholder={'Enter a dorm'}
                  button={'add dorm'}
                  onSubmit={text => {
                    this.setState({
                      ...this.state,
                      settings: {
                        ...this.state.settings,
                        college_to_dorm: {
                          ...this.state.settings.college_to_dorm,
                          [college]: [
                            ...this.state.settings.college_to_dorm[college],
                            text,
                          ],
                        },
                      },
                    });
                  }}
                />
                <br />
              </div>
            );
          })}
        </div>

        <div className="Box">
          <h3>Which Survey for College</h3>
          <br />
          {this.state.settings.colleges.map(college => {
            let survey = this.state.settings.college_to_survey[college];

            return (
              <div className="Box">
                <h4>{college}</h4>
                <select
                  value={survey || null}
                  onChange={event => {
                    this.setState({
                      settings: {
                        ...this.state.settings,
                        college_to_survey: {
                          ...this.state.settings.college_to_survey,
                          [college]: event.target.value,
                        },
                      },
                    });
                  }}
                >
                  {Object.keys(this.props.surveys).map(survey_key => {
                    if (this.props.surveys[survey_key] !== null) {
                      return (
                        <option key={survey_key} value={survey_key}>
                          {this.props.surveys[survey_key].name}
                        </option>
                      );
                    }
                  })}
                  <option key={'none'} value={null}>
                    {null}
                  </option>
                </select>
              </div>
            );
          })}
        </div>
        <div className="Box">
          <h3>Status by College</h3>
          <br />
          {this.state.settings.colleges.map(college => {
            let status = this.state.settings.status[college] || 'NONE';

            return (
              <div className="Box">
                <h4>{college}</h4>
                <select
                  value={status || null}
                  onChange={event => {
                    this.setState({
                      settings: {
                        ...this.state.settings,
                        status: {
                          ...this.state.settings.status,
                          [college]: event.target.value,
                        },
                      },
                    });
                  }}
                >
                  {this.state.settings.status_types.map(status_type => {
                    return (
                      <option key={status_type} value={status_type}>
                        {status_type === 'NONE' ? '' : status_type}
                      </option>
                    );
                  })}
                </select>
              </div>
            );
          })}
        </div>
        <button
          onClick={() => {
            this.props.updateSettings(this.state.settings);
            alert('Settings Updated');
          }}
          style={{
            opacity: !equal ? 1 : 0.7,
            pointerEvents: !equal ? 'auto' : 'none',
          }}
        >
          update settings
        </button>
      </div>
    );
  }
}
