import * as React from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { PrivateRoute, PublicRoute } from 'components/PrivateRoute';
import Loading from 'components/Loading';

import PageLanding from 'pages/PageLanding';
import PageOnboard from 'pages/PageOnboard';
import PageApp from 'pages/PageApp';
import PageAuth from 'pages/PageAuth';
import SchoolPortal from 'pages/SchoolPortal';

import { Route, Switch, Redirect } from 'react-router-dom';
import { bindActionCreators } from 'redux';
import { firestoreConnect, firebaseConnect } from 'react-redux-firebase';

class RootImpl extends React.Component {
  render() {
    if (!this.props.isLoaded) {
      return (
        <div style={{ maxWidth: 500, margin: '20% auto' }}>
          <Loading type="spin" />
        </div>
      );
    }

    return (
      <div className="Root">
        <Switch>
          <PrivateRoute
            path="/app"
            component={
              this.props.emailVerified
                ? this.props.onboarded
                  ? PageApp
                  : PageOnboard
                : PageAuth
            }
            isLoggedIn={this.props.isLoggedIn}
          />
          <PrivateRoute
            path="/onboard"
            component={PageOnboard}
            isLoggedIn={this.props.isLoggedIn}
          />
          <PublicRoute
            exact
            path="/(|about|support)/"
            component={PageLanding}
            isLoggedIn={this.props.isLoggedIn}
          />
          <Route
            exact
            path="/(login|register|auth|reset)/"
            component={PageAuth}
          />
          <Route path="/school_portal" component={SchoolPortal} />
          <Route component={() => <Redirect to="/" />} />
        </Switch>
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  // console.log(state, props);
  return {
    isLoaded: state.firebase.auth.isLoaded && state.firebase.profile.isLoaded,
    isLoggedIn: state.firebase.auth.uid ? true : false,
    onboarded: state.firebase.profile.onboarded,
    emailVerified: state.firebase.auth.emailVerified,
  };
};

function mapDispatchToProps(dispatch) {
  return bindActionCreators({}, dispatch);
}

export default compose(
  firebaseConnect(),
  firestoreConnect(),
  connect(mapStateToProps, mapDispatchToProps),
)(RootImpl);
