/** @jsx jsx **/

import { Component } from 'react';
import { jsx, css } from '@emotion/core';
import Dates from './Dates';

const rightSidebarStyle = css`
  overflow-y: scroll;
  position: fixed;
  width: 361px;
  height: 100vh;
  right: 0px;
  top: 0px;
  background: #f3f2f2;
  padding-left: 20px;
  padding-top: 50px;

  .heading {
    font-family: 'Apercu';
    font-size: 24px;
    line-height: 29px;
    color: #545353;
  }

  .user-image {
    width: 80px;
    height: 80px;
    background: url(${require('assets/loading.svg')});
    background-repeat: no-repeat;
    background-size: contain;
    background-position: center;
    margin-bottom: 10px;
    object-fit: cover;
  }

  .first-image {
    float: left;
    margin-right: 10px;
  }

  .description {
    font-size: 16px;
    line-height: 19px;
    color: #545353;
    margin-bottom: 10px;
    margin-right: 20px;
  }

  .description-bold {
    font-family: Apercu;
  }

  .submit-button {
    background: #bd574e;
    width: 190px;
    height: 30px;
    padding: 2px;
    font-family: Apercu;
    font-size: 14px;
    letter-spacing: 0.145em;
    text-decoration-line: underline;
    color: #fffbfb;
    margin-top: 10px;
  }

  .bottom-description {
    margin-top: 10px;
    font-size: 16px;
    line-height: 19px;
    margin-right: 20px;
  }

  .box-container {
    float: left;
    margin-right: 15px;
  }

  .box {
    float: left;
    margin-right: 5px;
    margin-top: 3.5px;
    width: 17px;
    height: 17px;
  }

  .box1 {
    background: #e9c8c5;
  }

  .box2 {
    background: #d3908a;
  }

  .box3 {
    background: #ffad87;
  }

  .key {
    margin-bottom: 10px;
  }
`;

export default class RightSidebar extends Component {
  render() {
    const { match, matchedUser, matchId, meUser } = this.props;

    return (
      <div css={rightSidebarStyle}>
        <img
          alt="profile"
          className="user-image first-image"
          src={matchedUser.profile_pic || require('assets/empty.png')}
        />
        <img
          alt="profile"
          className="user-image"
          src={meUser.profile_pic || require('assets/empty.png')}
        />

        <div className="heading">SCHEDULE FREE DATE</div>
        <div className="description">
          You’re eligible to redeem a free date with your top two matches.
          <br /> <br />
          <span className="description-bold">WHO: </span>
          {matchedUser.name} and {meUser.name} <br></br>
          <span className="description-bold">WHEN + WHERE: </span>Select when
          you're free
        </div>

        <div className="key">
          <div className="box-container">
            <div className="box box1" /> You
          </div>
          <div className="box-container">
            <div className="box box2" />
            {matchedUser.name.split(' ')[0]}
          </div>
          <div>
            <div className="box box3" />
            Both
          </div>
        </div>

        <Dates
          // simply stores the match_id; for example, 'match1', 'match2', etc
          matchId={matchId}
          // contains the information stored in 'match' node; for example, the data stored under 'match1' like user1, user2, etc
          match={match}
          // contains the ID of the matched person
          otherUid={matchedUser.id}
          // contains the ID of the user
          uid={meUser.id}
        />

        <button className="submit-button">SUBMIT AVAILABILITY</button>
        <div className="bottom-description">
          Once you both submit, you’ll be able to redeem a code for a time you
          can both make!
        </div>
      </div>
    );
  }
}
