import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Mixpanel } from 'utils/mixpanel';
import Loading from './Loading';
import AuthWrapper from './AuthWrapper';
import GoogleButton from './GoogleButton';
import PropTypes from 'prop-types';

export default class PageLogin extends Component {
  constructor(props) {
    super(props);
    this.state = { message: '', loading: false };
  }

  onFormSubmit = event => {
    event.preventDefault();

    const email = this.refs.email.value;
    const password = this.refs.password.value;

    this.setState({ loading: true });

    this.props
      .loginUser({ email, password })
      .then(data => {
        Mixpanel.identify(email);
        Mixpanel.track('user_login', { email: email });
      })
      .catch(error => {
        this.setState({ message: error.message, loading: false });
      });
  };

  loginWithProvider = provider => {
    this.setState({ loading: true });

    this.props
      .loginUser({ provider })
      .then(data => {
        let email = data.user.user.email;
        Mixpanel.identify(email);
        Mixpanel.track('user_login', { email: email });
      })
      .catch(error => {
        this.setState({ message: error.message, loading: false });
      });
  };

  render() {
    return (
      <div>
        <AuthWrapper>
          <form onSubmit={this.onFormSubmit}>
            <h1>Log In</h1>
            <br />
            <input
              type="email"
              ref="email"
              placeholder="@college.harvard.edu email"
            />
            <br />
            <br />
            <input type="password" ref="password" placeholder="password" />
            <br />
            <br />
            {!this.state.loading ? (
              <div>
                <button type="submit">Log In</button>
                <br />
                {this.state.message && (
                  <div className="Warning">
                    <br />
                    {this.state.message}
                  </div>
                )}
                <hr />
                <GoogleButton
                  onClick={() => {
                    this.loginWithProvider('google');
                  }}
                />
              </div>
            ) : (
              <Loading style={{ height: 148 }} />
            )}
          </form>
        </AuthWrapper>
        <div className={'AuthLinks'}>
          <Link to="/reset">Forgot password? Reset</Link>
          <br />
          <div style={{ height: 10 }} />
          <Link to="/register">Create an account</Link>
          <br />
        </div>
      </div>
    );
  }
}

PageLogin.propTypes = {
  loginUser: PropTypes.func.isRequired,
};
