import React, { Component } from 'react';
import Loading from 'components/Loading';
import Join from 'components/Join';
import { Link } from 'react-router-dom';
import { Converter } from 'showdown';

export default class PageSurveyLayout extends Component {
  renderMessage = completed => {
    if (this.props.status === 'live-survey') {
      return completed ? (
        <div>
          You've completed the survey! Make sure your{' '}
          <Link to="/app/profile">profile</Link> is up to date as we calculate
          your matches by <b>Friday, September 20</b>!
        </div>
      ) : (
        <div>Your survey is incomplete!</div>
      );
    } else if (this.props.status === 'live-processing') {
      return <div>The survey is closed! Check back soon for your matches!</div>;
    } else if (this.props.status === 'live-matches') {
      return (
        <div>
          The survey is closed!{' '}
          <Link to="/app/results">Check out your matches!</Link>
        </div>
      );
    }

    console.log('none');
  };

  render() {
    if (!this.props.responses_requested && !this.props.survey) {
      return (
        <div style={{ height: 300 }}>
          <Loading />
        </div>
      );
    }

    if (
      !this.props.survey ||
      !this.props.survey.name ||
      (!this.props.status.includes('live') &&
        this.props.status !== 'development' &&
        !this.props.super)
    ) {
      return <div>Currently, there's no survey open. Check back soon!</div>;
    }

    let questions = this.props.survey.questions;
    console.log(questions);
    let responses = this.props.responses || [];

    var items = Object.keys(questions).map(function(key) {
      return [key, questions[key].order];
    });

    // Sort the array based on the second element
    items.sort(function(first, second) {
      return first[1] - second[1];
    });

    var completed =
      Object.keys(responses).length === Object.keys(questions).length;

    return (
      <div className="PageSurvey">
        <div className="header">SURVEY</div>
        <br />
        <div>
          Donâ€™t overthink these questions. They will reveal things that you
          don't even know about yourself.
        </div>
        <br />
        <div>{this.renderMessage(completed)}</div>
        {items.map((item, index1) => {
          let question_num = item[0];
          let question = this.props.survey.questions[question_num];

          var items2 = Object.keys(question.answers || {}).map(function(key) {
            return [key, question.answers[key].order];
          });

          // Sort the array based on the second element
          items2.sort(function(first, second) {
            return first[1] - second[1];
          });
          let converter = new Converter();

          return (
            <div className="Question" key={question_num}>
              <div
                dangerouslySetInnerHTML={{
                  __html: converter.makeHtml('<h5>' + question.text + '</h5>'),
                }}
              />
              {items2.map((item2, index2) => {
                let answer_num = item2[0];
                let answer = question.answers[answer_num];
                let pressed =
                  // (responses && responses[question_num]) === answer_num;
                  (responses && responses[index1]) === index2;
                return (
                  <div
                    key={index2}
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                    }}
                    className={pressed ? 'answer answerpressed' : 'answer'}
                  >
                    <div
                      onClick={() => {
                        // this.props.updateResponse(question_num, answer_num);
                        this.props.updateResponse(
                          index1,
                          pressed ? null : index2,
                        );
                      }}
                      className="radio"
                    >
                      {pressed && <div className="radioinside" />}
                    </div>
                    <button
                      key={answer_num}
                      onClick={() => {
                        // this.props.updateResponse(question_num, answer_num);
                        this.props.updateResponse(
                          index1,
                          pressed ? null : index2,
                        );
                      }}
                      className={pressed ? 'pressed' : ''}
                      dangerouslySetInnerHTML={{
                        __html: converter.makeHtml(answer.text),
                      }}
                    ></button>
                  </div>
                );
              })}
            </div>
          );
        })}
        <div>{this.renderMessage(completed)}</div>
        <br />
        <Join />
      </div>
    );
  }
}
