/** @jsx jsx **/

import { Component } from 'react';
import { jsx, css } from '@emotion/core';
import { connect } from 'react-redux';
import { compose } from 'redux';
import {
  firestoreConnect,
  firebaseConnect,
  isLoaded,
} from 'react-redux-firebase';

import Header from 'components/Header';
import Loading from 'components/Loading';

const pageTeamStyle = css`
  .image-container {
    position: relative;
    float: left;
    margin-left: 5px;
    margin-bottom: 5px;
  }

  .team-image {
    width: 200px;
    height: 200px;
  }

  .image-hover {
    position: absolute;
    top: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    background: white;
    height: 200px;
    width: 200px;

    &:hover {
      opacity: 0.8;
    }
  }

  .overlay-text-container {
    text-align: center;
  }

  .overlay-text {
    display: block;
    margin-bottom: 5px;
  }
`;

class PageTeam extends Component {
  constructor(props) {
    super(props);
    this.state = { loading: true };
  }

  componentDidUpdate() {
    const { settings } = this.props;

    if (isLoaded(settings) && this.state.loading) {
      this.setState({
        loading: false,
        settings,
      });
    }
  }

  render() {
    const { loading, settings } = this.state;
    if (loading) {
      return (
        <div style={{ height: 200 }}>
          <Loading />
        </div>
      );
    }

    return (
      <div css={pageTeamStyle}>
        <Header>Team</Header>
        {Object.keys(settings).map(school => {
          if (school !== this.props.college) {
            return <div></div>;
          }
          return (
            <div key={school}>
              <h5>{school}</h5>
              {settings[school].map((member, index) => {
                return (
                  <div className="image-container" key={index}>
                    <img
                      alt="teamPic"
                      className="team-image"
                      src={member.image_url || require('assets/empty.png')}
                    />
                    <div className="image-hover">
                      <div className="overlay-text-container">
                        <div className="overlay-text">{member.name}</div>
                        <div className="overlay-text">{member.position}</div>
                        <div className="overlay-text">{member.phrase}</div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          );
        })}
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  return {
    settings: state.firestore.data.data,
  };
};

export default compose(
  firebaseConnect(),
  firestoreConnect(() => [
    {
      collection: 'team',
      doc: 'data',
      storeAs: 'data',
    },
  ]),
  connect(mapStateToProps),
)(PageTeam);
