/** @jsx jsx */

import { Component } from 'react';
import { jsx, css } from '@emotion/core';

const questionStyle = css`
  max-width: 700px;

  .radio {
    border-radius: 50%;
    border: 2px solid #bd574e;
    width: 20px;
    height: 20px;
    background: #dd8078;
    margin-right: 11px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .radio-inside {
    border-radius: 50%;
    width: 12px;
    height: 12px;
    background: #4f1f1f;
  }

  .answer {
    padding: 0px 50px;
    font-family: 'apercu-light';
    text-align: left;
    color: #222222;
    align-items: center;
    cursor: pointer;
    &:hover {
      background: rgba(196, 196, 196, 0.1);
    }
  }

  .answer-selected {
    background: #f3f2f2;
    &:hover {
      background: #f3f2f2;
    }
  }

  .question {
    margin-bottom: 10px;
    font-family: 'apercu-bold';
    font-size: 20px;
  }
`;

export default class SurveyQuestion extends Component {
  constructor(props) {
    super(props);
    this.state = { selectedIndex: null };
  }

  render () {
    const { question } = this.props;
    const { selectedIndex } = this.state;

    return (
      <div css={questionStyle}>
        <div className="question">{question.text}</div>
        {question.answers.map((text, answerIndex) => {
          const selected = selectedIndex === answerIndex;

          return (
            <div
              className={selected ? 'answer answer-selected' : 'answer'}
              onClick={() => selected 
                ? this.setState({ selectedIndex : null }) 
                : this.setState({ selectedIndex : answerIndex })}
            >
              <div className="radio">
                {selected && <div className="radio-inside" />}
              </div>
              {text}
            </div>
          );
        })}
      </div>
    );
  }
}
