import React, { Component } from 'react';
import { Route } from 'react-router-dom';

import UserPanel from './components/UserPanel';
import NotificationsPanel from './components/NotificationsPanel';
import AccountPanel from './components/AccountPanel';

class ContextLayout extends Component {
  constructor(props) {
    super(props);
    this.state = { content: 'component' };

    this.setWrapperRef = this.setWrapperRef.bind(this);
    this.handleClickOutside = this.handleClickOutside.bind(this);
    this.notifications = React.createRef();
    this.profile = React.createRef();
  }

  componentDidMount() {
    document.addEventListener('mouseup', this.handleClickOutside);
  }

  componentWillUnmount() {
    document.removeEventListener('mouseup', this.handleClickOutside);
  }

  setWrapperRef(node) {
    this.wrapperRef = node;
  }

  handleClickOutside(event) {
    if (this.wrapperRef && !this.wrapperRef.contains(event.target)) {
      this.setState({ content: 'component' });
    }
  }

  handleClickOutside(event) {
    if (
      this.wrapperRef &&
      !this.wrapperRef.contains(event.target) &&
      !this.notifications.current.contains(event.target) &&
      !this.profile.current.contains(event.target)
    ) {
      this.setState({ content: 'component' });
    }
  }

  renderRoute = () => {
    return <div style={{ position: 'relative', height: '100%' }}></div>;
  };

  renderContent = component => {
    if (this.state.content === 'notifications') {
      return <NotificationsPanel {...this.props} />;
    } else if (this.state.content === 'profile') {
      return <AccountPanel {...this.props} />;
    } else if (this.state.content === 'component') {
      return <UserPanel {...this.props} />;
    }
  };

  render() {
    return (
      <div>
        <div
          style={{
            display: 'flex',
            justifyContent: 'flex-end',
            paddingTop: 10,
          }}
        >
          <div
            ref={this.notifications}
            onClick={() => {
              this.setState(prev => {
                return {
                  content:
                    prev.content !== 'notifications'
                      ? 'notifications'
                      : 'component',
                };
              });
            }}
            style={{
              background:
                !this.props.docked && this.props.open
                  ? 'rgb(174, 174, 174)'
                  : 'inherit',
            }}
          >
            <i
              className={
                'fas fa-bell icon ' +
                (this.state.content === 'notifications' ? 'Highlight' : '')
              }
            ></i>
          </div>
          <div style={{ width: 20 }} />
          <div
            ref={this.profile}
            onClick={() => {
              this.setState(prev => {
                return {
                  content: prev.content !== 'profile' ? 'profile' : 'component',
                };
              });
            }}
            style={{
              background:
                !this.props.docked && this.props.open
                  ? 'rgb(174, 174, 174)'
                  : 'inherit',
            }}
          >
            <i
              className={
                'fas fa-user-circle icon ' +
                (this.state.content === 'profile' ? 'Highlight' : '')
              }
            ></i>
          </div>
        </div>
        <br />
        {this.state.content === 'profile' && (
          <div className="caret" style={{ left: 215.5 }}>
            <i className="fas fa-caret-up"></i>
          </div>
        )}
        {this.state.content === 'notifications' && (
          <div className="caret" style={{ left: 174.3 }}>
            <i className="fas fa-caret-up"></i>
          </div>
        )}
        <div ref={this.setWrapperRef} className={'Panel'}>
          {this.renderContent(this.props.component)}
        </div>
      </div>
    );
  }
}

export default ContextLayout;
