import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firestoreConnect, firebaseConnect } from 'react-redux-firebase';
import PageSettingsLayout from './layout';
import './styles.scss';

class PageSettings extends Component {
  render() {
    return <PageSettingsLayout {...this.props} />;
  }
}

const mapStateToProps = (state, props) => {
  var user = props.firebase.auth().currentUser;
  return {
    email: state.firebase.auth.email,
    resetPassword: props.firebase.resetPassword,
    auth: props.firebase.auth,
    updateProfile: props.firebase.updateProfile,
    profile: state.firebase.profile,
    deleteUser: () => {
      // props.firebase.update({ path: 'users/' + state.firebase.auth.uid }, null);
      props.firebase.auth().currentUser.delete();
    },
    reauthenticate: password => {
      const credential = props.firebase.auth.EmailAuthProvider.credential(
        state.firebase.auth.email,
        password,
      );

      return user.reauthenticateWithCredential(credential);
    },
  };
};

export default compose(
  firebaseConnect(),
  firestoreConnect(),
  connect(mapStateToProps),
)(PageSettings);
