import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import GoogleButton from 'components/GoogleButton';
import Loading from 'components/Loading';
import Logo from 'components/Logo';

export default class PageRegister extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: '',
      loading: false,
    };
  }

  onFormSubmit = event => {
    event.preventDefault();

    const email = this.refs.email.value;
    const password = this.refs.password.value;
    const confirmPassword = this.refs['confirm-password'].value;

    this.setState({ loading: true });

    if (password === confirmPassword) {
      this.props
        .registerUser({ email, password })
        .then(data => {
          this.props.firebase
            .auth()
            .currentUser.sendEmailVerification()
            .then(() => {
              console.log('verification email sent');
            })
            .catch(err => {
              console.log(err);
            });
        })
        .catch(error => {
          this.setState({ message: error.message, loading: false });
        });
      // Mixpanel.alias(email);
      // Mixpanel.track('user_signup', { email: email });
    } else {
      this.setState({ message: 'Passwords do not match', loading: false });
    }
  };

  loginWithProvider = provider => {
    this.setState({ loading: true });

    this.props
      .loginUser({ provider })
      // .then(data => {
      //   let email = data.user.user.email;
      // })
      .catch(error => {
        this.setState({ message: error.message, loading: false });
      });
  };

  render() {
    return (
      <div className="PageRegister">
        <Logo login center />
        <div className="Box" style={{ width: '100%' }}>
          <form onSubmit={this.onFormSubmit}>
            <h1>Sign Up</h1>
            <br />
            <input
              type="email"
              ref="email"
              placeholder="@college.harvard.edu email"
            />
            <br />
            <br />
            <input type="password" ref="password" placeholder="password" />
            <br />
            <br />
            <input
              type="password"
              ref="confirm-password"
              placeholder="confirm password"
            />
            <br />
            <br />
            {!this.state.loading ? (
              <div>
                <button type="submit">Sign Up</button>
                <br />
                {this.state.message && (
                  <div className="Warning">
                    <br />
                    {this.state.message}
                  </div>
                )}
                {/*<div style={{fontSize: 12, marginTop: 10, marginBottom: 10}}> or </div>*/}
                <hr />
                <GoogleButton
                  onClick={() => {
                    this.loginWithProvider('google');
                  }}
                  text={'Sign up with Google'}
                />
              </div>
            ) : (
              <Loading style={{ height: 148 }} />
            )}
          </form>
        </div>
        <br />
        <Link to="/login">Already have an account? Log in</Link>
        <br />
        <div style={{ height: 10 }} />
        <Link to="/reset">Forgot password? Reset</Link>
        <br />
      </div>
    );
  }
}
