import React, { Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators, compose } from 'redux';
import PageAppLayout from './layout';
import { firestoreConnect, firebaseConnect } from 'react-redux-firebase';
import { isLoaded } from 'react-redux-firebase';
import Loading from 'components/Loading';

class PageApp extends Component {
  render() {
    if (!isLoaded(this.props.settings)) {
      return (
        <div style={{ height: 200 }}>
          <Loading />
        </div>
      );
    }

    var email = this.props.email.split('@')[1];
    var college = this.props.settings.email_to_college[email];
    var survey = this.props.settings.college_to_survey[college] || 'NONE';
    var status = this.props.settings.status[college] || 'offline';
    var dorms = this.props.settings.college_to_dorm[college] || [null];

    return (
      <PageAppLayout
        {...this.props}
        email={email}
        college={college}
        survey={survey}
        status={status}
        dorms={dorms}
      />
    );
  }
}

const mapStateToProps = (state, props) => {
  return {
    uid: state.firebase.auth.uid,
    admin: state.firebase.profile.admin,
    settings: state.firestore.data.settings,
    email: state.firebase.auth.email,
    name: state.firebase.profile.first,
  };
};

function mapDispatchToProps(dispatch) {
  return bindActionCreators({}, dispatch);
}

export default compose(
  firebaseConnect(),
  firestoreConnect(props => [
    {
      collection: 'settings',
      doc: 'config',
      storeAs: 'settings',
    },
  ]),
  connect(mapStateToProps, mapDispatchToProps),
)(PageApp);
