/** @jsx jsx */

import { jsx, css } from '@emotion/core';

const questionStyle = css`
  max-width: 700px;

  .radio {
    border-radius: 50%;
    border: 2px solid #bd574e;
    width: 20px;
    height: 20px;
    background: #dd8078;
    margin-right: 11px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .radio-inside {
    border-radius: 50%;
    width: 12px;
    height: 12px;
    background: #4f1f1f;
  }

  .answer {
    padding: 0px 10px;
    font-family: 'apercu-light';
    height: 40px;
    text-align: left;
    color: #222222;
    display: flex;
    align-items: center;
    cursor: pointer;
    &:hover {
      background: rgba(196, 196, 196, 0.1);
    }
  }

  .answer-selected {
    background: #f3f2f2;
    &:hover {
      background: #f3f2f2;
    }
  }

  .question {
    margin-bottom: 15px;
    font-family: 'apercu-bold';
    font-size: 30px;
  }

  .save-changes {
    align-items: center;
    background: #bd574e;
    color: #ffffff;
    font-family: Apercu;
    font-size: 18px;
    height: 38px;
    letter-spacing: 0.05em;
    margin-top: 20px;
    width: 170px;
  }

  .checkmark {
    margin-right: 4px;
  }

  .incomplete-message {
    color: red;
  }
`;

function SurveyQuestion(props) {
  const { question } = props;
  return (
    <div css={questionStyle}>
      <div className="question">{question.text}</div>
      <ul>
        {question.answers.map(answer => (
          <li key={answer.order}>{answer}</li>
        ))}
      </ul>
    </div>
  );
}

export default SurveyQuestion;
