import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Fade from 'react-reveal/Fade';

import PicUpload from 'components/PicUpload';

export default class Page2 extends Component {
  constructor(props) {
    super(props);
    this.file = React.createRef();
    this.state = { pic: null };
  }

  render() {
    const { email, profile_pic, updateProfile } = this.props;
    var filled = true;
    return (
      <Fade>
        <Container style={{ maxWidth: 600 }}>
          <h1>Profile Picture</h1>
          <br />
          <PicUpload
            name={email}
            original_pic={profile_pic}
            path="/profile_pics"
            size={200}
            updateURL={updateProfile}
          />
          <br />
          <br />
          <Row>
            <Col xs={6}>
              <div
                onClick={() => this.props.handlePageChange(1)}
                style={{
                  marginTop: 10,
                  width: '100%',
                  padding: '10px 14px 10px 14px',
                  cursor: 'pointer',
                  // background: "#5558EA", borderRadius: 40, color: "white"
                }}
              >
                PREV
              </div>
            </Col>
            <Col xs={6}>
              <button
                onClick={() => this.props.handlePageChange(filled ? 3 : 2)}
                style={{
                  marginTop: 10,
                  width: '100%',
                  opacity: filled ? 1 : 0.8,
                  pointerEvents: filled ? 'auto' : 'none',
                }}
              >
                NEXT
              </button>
            </Col>
          </Row>
        </Container>
      </Fade>
    );
  }
}
