/** @jsx jsx **/

import { Component } from 'react';
import { jsx, css } from '@emotion/core';
import Firebase from 'firebase';

import Header from 'components/Header';
import Loading from 'components/Loading';

const checkInStyle = css`
  font-weight: bold;

  .conf-container {
    margin: 20px auto;
  }

  p {
    margin-bottom: 1em;
  }

  button {
    max-width: 300px;
    margin-right: 1em;
    margin-bottom: 1em;
    padding: 0.1em;
  }

  input {
    max-width: 359px;
    height: 40px;
    border: none;
    background: #f4f2f2;
    font-family: 'Apercu-light';
    display: flex;
    align-items: center;
    letter-spacing: 0.05em;

    color: #545353;
  }
`;

const testDateId = {
  datesAvailable: 49,
  day: "12/28",
  place: "Jefe's",
};

export default class ConfirmationCodes extends Component {

  constructor(props) {
    super(props);

    this.state = {
      code: '',
      loading: true,
      display: false,
      errorMessage: '',
      valid: null,
    };
  }

  getCodesByDay = day => {
    let codeSnapshot = this.props.firebase
      .ref('codes')
      .orderByChild('date')
      .equalTo(day)
      .once('value');
    codeSnapshot.on('value', snapshot => {
      const codes = codeSnapshot.val()
      this.setState({ loading: false, state: codes});
      console.log("Data loaded")
    })
  }

  componentDidMount() {
    this.getCodesByDay();
  }

  render() {
    const dateId = this.props.dateId || testDateId;
    const { day, place } = dateId;
    const codes = []
    return (
      <div className="RestaurantHome" css={checkInStyle}>
        <Header>{day} {place.toUpperCase()} CONFIRMATION CODES</Header>
        <div>
        <b>Confirmation Codes</b>
        <table>
          <tr>
            <th><b>Code</b></th>
          </tr>
          {codes.map(code => 
            <tr>
              <td>{code.getKey()}</td>
            </tr>
          )}
        </table>
      </div>
      </div>
    );
  }
}
