import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { withRouter } from 'react-router';
import { firestoreConnect, firebaseConnect } from 'react-redux-firebase';
import { isLoaded } from 'react-redux-firebase';

import Loading from 'components/Loading';
import PageAppLayout from './layout';

class PageApp extends Component {
  render() {
    if (!isLoaded(this.props.settings)) {
      return (
        <div style={{ margin: '10% auto' }}>
          <Loading size={200} type="spin" />
        </div>
      );
    }

    var suffix = this.props.email.split('@')[1];
    var college = this.props.settings.email_to_college[suffix];
    var survey = this.props.settings.college_to_survey[college] || 'NONE';
    var status = this.props.settings.status[college] || 'offline';
    var dorms = this.props.settings.college_to_dorm[college] || [null];

    return (
      <PageAppLayout
        {...this.props}
        dorms={dorms}
        status={status}
        survey={survey}
      />
    );
  }
}

const mapStateToProps = (state, _props) => {
  return {
    admin: state.firebase.profile.admin,
    email: state.firebase.auth.email,
    settings: state.firestore.data.settings,
    uid: state.firebase.auth.uid,
  };
};

export default compose(
  firebaseConnect(),
  firestoreConnect(() => [
    {
      collection: 'settings',
      doc: 'config',
      storeAs: 'settings',
    },
  ]),
  withRouter,
  connect(mapStateToProps),
)(PageApp);
