/*

Framework created by Ryan Lee, use only allowed with permission from Ryan Lee.

*/

import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.css';
import 'styles/styles.scss';
import App from './App';
import * as serviceWorker from './serviceWorker';

import { applyMiddleware, createStore, compose } from 'redux';
import { createBrowserHistory } from 'history';
import createSagaMiddleware from 'redux-saga';
import { routerMiddleware } from 'connected-react-router';

import { getFirebase } from 'react-redux-firebase';
import { createFirestoreInstance } from 'redux-firestore';

import firebase from 'firebase';
import 'firebase/auth';
import 'firebase/storage';
import 'firebase/firestore';
import { FIREBASE_CONFIG } from 'constants/Firebase';

import './assets/fonts/apercu_regular_pro.otf';
import './assets/fonts/apercu_medium_pro.otf';
import './assets/fonts/apercu_bold_pro.otf';

import rootReducer from 'store/reducers';
import root from 'store/sagas';

require('dotenv').config();

firebase.initializeApp(FIREBASE_CONFIG);

const history = createBrowserHistory();
const sagaMiddleware = createSagaMiddleware();

let middleware = [routerMiddleware(history), sagaMiddleware];

const rrfConfig = {
  userProfile: 'users',
  // useFirestoreForProfile: true, // Firestore for Profile instead of Realtime DB
  useFirestoreForStorageMeta: true,
  dispatchRemoveAction: true,
};

const store = createStore(
  rootReducer,
  {},
  compose(
    applyMiddleware(...middleware),
    typeof window === 'object' &&
      typeof window.devToolsExtension !== 'undefined'
      ? () => window.__REDUX_DEVTOOLS_EXTENSION__
      : f => f,
  ),
);

const rrfProps = {
  firebase: firebase,
  config: rrfConfig,
  dispatch: store.dispatch,
  createFirestoreInstance, // <- needed if using firestore
};

sagaMiddleware.run(root, getFirebase);

ReactDOM.render(
  <App store={store} rrfProps={rrfProps} />,
  document.getElementById('root'),
);

serviceWorker.unregister();
