import React, { Component } from 'react';

import moment from 'moment';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Fade from 'react-reveal/Fade';
import Toggle from 'react-toggle';

export default class Page1 extends Component {
  renderInput = (name, type, placeholder, value, size = 6) => {
    return (
      <Col sm={size} xs={12} style={{ paddingBottom: 20 }}>
        <input
          name={name}
          type={type}
          placeholder={placeholder}
          value={value}
          onChange={this.props.handleInputChange}
        />
      </Col>
    );
  };

  renderTextarea = (name, type, placeholder, value, size = 6) => {
    return (
      <Col sm={size} xs={12} style={{ paddingBottom: 20 }}>
        <textarea
          rows="4"
          maxlength="300"
          name={name}
          type={type}
          placeholder={placeholder}
          value={value}
          onChange={this.props.handleInputChange}
        />
      </Col>
    );
  };

  renderSelect = (name, value, size = 6) => {
    return (
      <Col sm={size} xs={12} style={{ paddingBottom: 20 }}>
        <select
          name={name}
          value={value}
          onChange={this.props.handleInputChange}
        >
          <option value="">DORM (required)</option>
          {this.props.dorms.map(dorm => {
            return <option value={dorm}>{dorm}</option>;
          })}
        </select>
      </Col>
    );
  };

  render() {
    var filled =
      this.props.name !== '' &&
      // this.props.last !== '' &&
      this.props.dorm !== ''; //&&
    // this.props.description !== '';
    // this.props.birthday &&
    // moment().diff(this.props.birthday, 'years') > 13; //&&
    // (/^\d{5}(-\d{4})?$/.test(this.props.zipcode))

    return (
      <Fade>
        <Container style={{ maxWidth: 600, padding: 20 }}>
          <h1>Basic Info</h1>
          <div>You can update this anytime!</div>
          <br />
          <Row>
            {this.renderInput(
              'name',
              'text',
              'Your full name (required)',
              this.props.name,
              12,
            )}
            {/*this.renderInput('last', 'text', 'Last Name', this.props.last)*/}
          </Row>
          <Row>{this.renderSelect('dorm', this.props.dorm, 12)}</Row>
          <Row>
            {this.renderTextarea(
              'description',
              'text',
              'A bit about yourself! (300 chars or less)',
              this.props.description,
              12,
            )}
          </Row>
          {/*<Row>
            {this.renderInput(
              'birthday',
              'date',
              this.props.birthday ? '  ' : 'Birthday\xa0\xa0',
              this.props.birthday,
              12
            )}
          </Row>*/}
          {/* this.renderInput("zipcode", "number", "HOME ZIPCODE", this.props.zipcode, 12) */}
          <Row>
            <Col>
              <button
                onClick={e => {
                  e.preventDefault();
                  this.props.handlePageChange(filled ? 2 : 1);
                }}
                style={{
                  marginTop: 10,
                  width: '100%',
                  opacity: filled ? 1 : 0.8,
                  pointerEvents: filled ? 'auto' : 'none',
                }}
              >
                NEXT
              </button>
            </Col>
          </Row>
        </Container>
      </Fade>
    );
  }
}
