import React, { Component } from 'react';
import { isLoaded, isEmpty } from 'react-redux-firebase';
import Loading from 'components/Loading';
import { Link } from 'react-router-dom';

export default class PageHomeLayout extends Component {
  renderMessage = () => {
    const { responses, survey, matches } = this.props;

    if (this.props.status === 'live-survey') {
      return (
        <div>
          Fill out <Link to="/app/survey">the survey!</Link>
        </div>
      );
    } else if (this.props.status === 'live-processing') {
      return (
        <div>
          We're calculating your matches. Spruce up{' '}
          <Link to="/app/profile">your profile</Link> before they are released!
        </div>
      );
    } else if (this.props.status === 'live-matches') {
      return (
        <div>
          Check out <Link to="/app/results">your matches!</Link>
        </div>
      );
    }
  };

  // transform: rotate(45deg)
  render() {
    return (
      <div className="PageHome">
        {/* <div className="header">HOME</div> */}
        {/* <br /> */}
        {this.renderMessage()}
        <br />
        {this.state.dorm || 'Hover over a dorm'}
      </div>
    );
  }
}
