import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firebaseConnect, firestoreConnect } from 'react-redux-firebase';
import { Route, Switch, Redirect } from 'react-router-dom';

import Sidebar from 'components/Sidebar';
import Loading from 'components/Loading';
import PageLogin from 'components/Login';

import NavigationBar from './NavigationBar';
import CheckIn from './CheckIn';

class RestaurantPortal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      restaurant: props.profile.restaurantAdmin,
    };
  }

  async componentDidMount() {
    const { firebase, profile } = this.props;
    let restaurant = profile.restaurantAdmin;

    // if we aren't 100% sure this person is a restaurantAdmin
    if (!restaurant) {
      const { data } = await firebase
        .functions()
        .httpsCallable('user-makeRestaurantAdmin')();
      restaurant = data;
    }

    this.setState({ loading: false, restaurant });
  }

  render() {
    if (this.state.loading) {
      return (
        <div style={{ height: 200 }}>
          <Loading />
        </div>
      );
    }

    const { isLoggedIn, loginUser } = this.props;
    const { restaurant } = this.state;

    if (!isLoggedIn) {
      return <PageLogin loginUser={loginUser} title="Restaurant Portal" />;
    }

    if (!restaurant) {
      return <Redirect to="/" />;
    }

    return (
      <div className="PageRestaurant">
        <Sidebar leftSidebar={<NavigationBar />}>
          <Switch>
            <Route
              exact
              path="/restaurant"
              render={() => {
                return <h1>You are in!</h1>;
              }}
            />
            <Route
              exact
              path="/restaurant/check-in"
              render={() => {
                return <CheckIn restaurant={restaurant} {...this.props} />;
              }}
            />
            <Route component={() => <Redirect to="/restaurant" />} />
          </Switch>
        </Sidebar>
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  return {
    isLoggedIn: state.firebase.auth.uid ? true : false,
    loginUser: props.firebase.login,
    profile: state.firebase.profile,
  };
};

export default compose(
  firebaseConnect(),
  firestoreConnect(),
  connect(mapStateToProps),
)(RestaurantPortal);
