import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Chat from '../components/Chat';

export default class PageMatch extends Component {
  state = { message: '' };

  render() {
    let match = this.props.matches[this.props.match];

    if (!match) {
      return <div>loading match...</div>;
    }

    // let user1 = match.user1;
    // let user2 = match.user2;
    let user = match.user1.id === this.props.uid ? match.user2 : match.user1;
    return (
      <div className="PageMatch">
        <div className="header">Schedule your meetup!</div>
        <Link to="/app/results">back</Link>
        <br />
        <br />
        <div style={{ background: '#F2F2F2', maxWidth: 820, margin: '0 auto' }}>
          <div style={{ display: 'flex', padding: 20, maxWidth: 600 }}>
            <img
              alt="user profile pic"
              src={user.profile_pic || require('assets/empty.png')}
              style={{
                width: 125,
                height: 125,
                objectFit: 'cover',
                minWidth: 125,
              }}
            />
            <div className={'info'}>
              <div className={'dorm'}>{user.dorm.toUpperCase()}</div>
              <div className="name">{user.name}</div>
              <div className="description">{user.description}</div>
              {/*<div>{match.rating}</div>*/}
            </div>
            <br />
            <br />
          </div>
        </div>
        <br />
        <div
          style={{
            background: '#F2F2F2',
            maxWidth: 820,
            margin: '0 auto',
            padding: 20,
            textAlign: 'center',
            marginBottom: -8,
          }}
        >
          You and {user.name} have a{' '}
          <span style={{ fontFamily: 'apercu' }}>
            {Math.round(match.rating)}%
          </span>{' '}
          compatability rating! Drag over your free times in the scheduling
          tool, choose a convenient location in the poll, and use the chat for
          further coordination! A red dot indicates an unread chat message!
        </div>
        <div
          style={{
            position: 'fixed',
            bottom: 0,
            right: 20,
            minWidth: 300,
            width: '50%',
            zIndex: 20,
            maxWidth: 400,
          }}
        >
          <Chat
            match={this.props.match}
            matchObject={match}
            name={user.name}
            otherUid={user.id}
            uid={this.props.uid}
            unreadMessage={this.props.unreadMessage}
          />
        </div>
        <br />
        <div style={{ overflow: 'hidden' }}>
          {
            <iframe
              src={
                'https://happytomeet.com/datamatch&' +
                //this.props.survey +
                this.props.match +
                '&' +
                this.props.name.replace(/ /g, '')
              }
              style={{
                width: 'calc(100% + 300px)',
                height: 710,
                overflow: 'visible',
                marginLeft: -150,
                position: 'relative',
                top: -60,
                marginBottom: -30,
                background: `url(${require('assets/loading.svg')})`,
                backgroundSize: 'contain',
                backgroundRepeat: 'no-repeat',
                backgroundPosition: 'center',
              }}
              width="100%"
              scrolling="no"
              frameBorder="0"
              title="happy to meet"
            />
          }
        </div>
        {/*<iframe
          src={"https://tlk.io/datamatch-" + this.props.match}
          width="100%"
          height="400px"
        />*/}

        {/*<div 
          id="tlkio" 
          data-channel={'datamatchchat'}
          data-theme="theme--minimal" 
          style={{width:"100%", height:"400px"}}>
        </div>*/}
      </div>
    );
  }
}
