/** @jsx jsx **/

import { Component } from 'react';
import { jsx, css } from '@emotion/core';

import Header from 'components/Header';
import Loading from 'components/Loading';

import { isEmpty } from 'react-redux-firebase';

const checkInStyle = css`
  font-weight: bold;

  .conf-container {
    margin: 20px auto;
  }

  p {
    margin-bottom: 1em;
  }

  button {
    max-width: 300px;
    margin-right: 1em;
    margin-bottom: 1em;
    padding: 0.1em;
  }

  input {
    max-width: 359px;
    height: 40px;
    border: none;
    background: #f4f2f2;
    font-family: 'Apercu-light';
    display: flex;
    align-items: center;
    letter-spacing: 0.05em;

    color: #545353;
  }
`;

const testDateId = {
  datesAvailable: 49,
  day: "12/28",
  place: "Jefe's",
};

export default class ConfirmationCodes extends Component {

  constructor(props) {
    super(props);

    this.state = {
      code: '',
      loading: true,
      display: false,
      errorMessage: '',
      valid: null,
    };
  }

  searchCodesByDay = day =>
    this.props.firebase
      .ref('codes')
      .orderByChild('date')
      .equalTo(day)
      .once('value');

  renderConfirmationCodes() {
    const dateSchedule = this.props.confirmationCodes;
    return (
      <div>
        <b>Date schedule:</b>
        <table>
          <tr>
            <th><b>Date</b></th>
            <th><b>Claimed</b></th>
            <th><b>Allotted</b></th>
          </tr>
          {dateSchedule.map(date => 
            <tr>
              <td>{date.date}</td>
              <td>{date.claimed}</td>
              <td>{date.allotted}</td>
            </tr>
          )}
          <tr>
              <td>Total</td>
              <td>{dateSchedule.reduce((total, num) => total + num.claimed, 0)}</td>
              <td>{dateSchedule.reduce((total, num) => total + num.allotted, 0)}</td>
          </tr>
        </table>
      </div>
    );
  }

  getCodesByDay = async (day) => {
    this.setState({ loading: true });
    const codes = await this.props.firebase
      .ref('codes')
      .orderByChild('date')
      .equalTo(day)
      .once('value');
    this.setState({ loading: false, });
    return codes
  };

  render() {
    const dateId = this.props.dateId || testDateId;
    const { day, place } = dateId;
    const codes = this.getCodesByDay(day);
    console.log(codes)
    return (
      <div className="RestaurantHome" css={checkInStyle}>
        <Header>{day} {place.toUpperCase()} CONFIRMATION CODES</Header>
      </div>
    );
  }
}
