const checkInStyle = css`
  font-weight: bold;

  .conf-container {
    margin: 20px auto;
  }

  button {
    max-width: 300px;
    margin-right: 0.5em;
    padding: 0.1em;
  }

  input {
    max-width: 359px;
    height: 40px;
    border: none;
    background: #f4f2f2;
    font-family: 'Apercu-light';
    display: flex;
    align-items: center;
    letter-spacing: 0.05em;

    color: #545353;
  }
`;

export default class RestaurantHome extends Component {
  constructor(props) {
    super(props);

    this.state = {
      code: '',
      display: false,
      errorMessage: '',
      valid: null,
    };
  }

  render() {
    return (
      <div className="CheckIn" css={checkInStyle}>
        <Header>RESTAURANT PORTAL</Header>
        <p>
          <b>Welcome to the restaurant portal!</b>
          <br />
          You can print confirmation codes below, or see the agreement you negotiated with the Datamatch team in more detail.
          <br />
          Contact cupids@datamatch.me if youâ€™d like to make changes.
        </p>

        <a href='/restaurant/check-in'><button>CHECK-IN ONLINE</button></a>
        <button>PRINT CONFIRMATION CODES</button>



        <br />
      </div>
    );
  }
}
