/** @jsx jsx */

import { jsx, css } from '@emotion/core';
import { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firebaseConnect } from 'react-redux-firebase';
import Toggle from 'react-toggle';
import 'react-toggle/style.css';

import Checkboxes from 'components/Checkboxes';
import Header from 'components/Header';
import Input from 'components/Input';
import PicUpload from 'components/PicUpload';
import Radios from 'components/Radios';
import Select from 'components/Select';
import Textarea from 'components/Textarea';

const pageProfileLayoutStyle = css`
  .profile-container {
    margin-top: 10px;
  }

  .profile-pic {
    float: left;
    margin-right: 10px;
  }

  .profile-input {
    text-align: left;
    width: 300px;
    height: 43px;
    margin-bottom: 9px;
    color: #545353;
    background: #f4f2f2;
    border: 0px;
  }

  .profile-select {
    background: #f4f2f2;
    background-image: url(https://image.flaticon.com/icons/svg/60/60781.svg);
    background-position: 95% 50%;
    background-repeat: no-repeat;
    background-size: 16px;
    border: 0px;
    width: 300px;
    height: 43px;
    margin-bottom: 9px;
    color: #545353;
  }

  .bottom-elements {
    margin-bottom: 20px;
  }

  .question-header {
    font-family: Apercu;
    letter-spacing: 0.05em;
  }

  .profile-text-area {
    text-align: left;
    width: 300px;
    height: 75px;
    color: #545353;
    background: #f4f2f2;
    border: 0px;
  }

  .text-area-container {
    display: inline-block;
  }

  .save-button {
    margin-left: 210px;
    width: 300px;
  }

  .incomplete-message {
    margin-top: 5px;
    margin-left: 210px;
    color: red;
  }

  .react-toggle--checked .react-toggle-track {
    background-color: #bd574e;
  }

  .react-toggle--checked:hover:not(.react-toggle--disabled)
    .react-toggle-track {
    background-color: #6c2c2c;
  }

  .react-toggle--checked .react-toggle-thumb {
    border-color: #bd574e;
  }
`;

class PageProfile extends Component {
  constructor(props) {
    super(props);
    const {
      description,
      dorm,
      email,
      gender,
      lookingFor,
      lookingForGender,
      matchType,
      name,
      show,
      year,
    } = props.profile;

    let harvardMIT;
    if (props.profile.harvardMIT) {
      harvardMIT = { Yes: true };
    } else {
      harvardMIT = { Yes: false };
    }

    this.state = {
      description,
      dorm,
      email,
      gender,
      harvardMIT,
      lookingFor,
      lookingForGender,
      matchType,
      name,
      show,
      year,
    };
  }

  handleInputChange = event => {
    const { name, value } = event.target;
    console.log(value)
    this.setState({ [name]: {'Male': true, 'Female': false, 'Nonbinary': true} });
  };

  handleCheckboxChange = (name, response) => {
    const values = this.state[name];
    const bool = values ? !values[response] : true;
    const newValues = {
      ...values,
      [response]: bool,
    };
    console.log(newValues)
    this.setState({ [name]: newValues });
  };

  handleRadioChange = (name, response) => {
    this.setState({ [name]: response });
  };

  handleToggleChange = () => {
    this.setState({ show: !this.state.show });
  };

  checkRequired = () => {
    const required = ['dorm', 'gender', 'matchType', 'name', 'year'];

    let completed = required.every(requirement => this.state[requirement]);

    const { lookingForGender } = this.state;
    const { Male, Female, Nonbinary } = lookingForGender || {};
    const lookingFor = this.state.lookingFor || {};

    // Using bracket notation as 'true love' is not a valid identifier
    const truelove = lookingFor['true love'];
    const bestie = lookingFor['bestie'];

    completed =
      completed && (Male || Female || Nonbinary) && (truelove || bestie);

    return completed;
  };

  checkChanged = () => {
    const { profile } = this.props;
    const { Male, Female, Nonbinary } = this.state.lookingForGender || {};
    const lookingForGender = profile.lookingForGender || {};
    const lookingFor = profile.lookingFor || {};
    const lookingForState = this.state.lookingFor || {};

    const harvardMIT = profile.harvardMIT || false;
    const { Yes } = this.state.harvardMIT;

    // Using bracket notation as 'true love' is not a valid identifier
    const truelove = lookingForState['true love'];
    const bestie = lookingForState['bestie'];

    const needCheck = [
      'description',
      'dorm',
      'gender',
      'matchType',
      'name',
      'show',
      'year',
    ];

    let same = needCheck.every(input => this.state[input] === profile[input]);

    // ^ is XOR. We need it to deal with undefined === false cases.
    same =
      same &&
      !(Male ^ lookingForGender['Male']) &&
      !(Female ^ lookingForGender['Female']) &&
      !(Nonbinary ^ lookingForGender['Nonbinary']) &&
      !(truelove ^ lookingFor['true love']) &&
      !(bestie ^ lookingFor['bestie']) &&
      harvardMIT === Yes;

    return same;
  };

  saveProfile = () => {
    if (this.checkRequired()) {
      const { college, updateProfile } = this.props;
      const lowerCaseName = this.state.name.toLowerCase();
      const { harvardMIT, show } = this.state;

      updateProfile({
        ...this.state,
        harvardMIT:
          college === 'Harvard' || college === 'MIT'
            ? harvardMIT && harvardMIT.Yes
            : null,
        last: show ? lowerCaseName.split(' ').slice(-1)[0] : null,
        searchName: show ? lowerCaseName : null,
      });
      alert('Info saved!');
    }
  };

  render() {
    const { college, email, profile_pic, updateProfile } = this.props;

    var filled = this.checkRequired();
    var changed = this.checkChanged();
    return (
      <div className="PageProfile" css={pageProfileLayoutStyle}>
        <Header>PROFILE</Header>
        <br />
        <div className="profile-container">
          <div>
            <div className="profile-pic">
              <PicUpload
                name={email}
                original_pic={profile_pic}
                path="/profile_pics"
                size={200}
                updateURL={updateProfile}
              />
            </div>
            <Input
              className="profile-input"
              handleInputChange={this.handleInputChange}
              name="name"
              placeholder="Your name"
              type="text"
              value={this.state.name || ''}
            />
            <Select
              className="profile-select"
              handleInputChange={this.handleInputChange}
              name="dorm"
              placeholder="Dorm"
              value={this.state.dorm}
              values={this.props.dorms}
            />
            <Select
              className="profile-select"
              handleInputChange={this.handleInputChange}
              name="year"
              placeholder="Year"
              value={this.state.year}
              values={[
                'First Year',
                'Sophomore',
                'Junior',
                'Senior',
                'Grad student',
              ]}
            />
            <div className="text-area-container">
              <Textarea
                className="profile-text-area"
                handleInputChange={this.handleInputChange}
                name="description"
                placeholder="Write a bit about yourself!"
                rows={2}
                type="text"
                value={this.state.description}
              />
            </div>
            <div className="bottom-elements">
              <div className="question-header">WHAT DO YOU WANT? (BE HONEST.)</div>
              I'm looking for
              <Select
                className="profile-select"
                handleInputChange={this.handleInputChange}
                name="lookingFor"
                placeholder="Select"
                value={this.state.lookingFor}
                labels={['love', 'friendship', 'anything, really', ]}
                values={[
                  {'true love': true,'bestie': false},
                  {'true love': false,'bestie': true},
                  {'true love': true,'bestie': true},
                ]}
              />
              with someone who is
              <Select
                className="profile-select"
                handleInputChange={this.handleInputChange}
                name="matchType"
                placeholder="Select"
                value={this.state.matchType}
                labels={['similar to me', 'different from me', ]}
                values={[ 'similar', 'different', ]}
              />
            </div>
            <div className="bottom-elements">
              <div className="question-header">GENDER PREFERENCES</div>
              I identify as
              <Select
                className="profile-select"
                handleInputChange={this.handleInputChange}
                name="gender"
                placeholder="Select"
                value={this.state.gender}
                labels={['male','female','nonbinary',]}
                values={['Male','Female','Nonbinary',]}
              />
              and I want to be matched with
              <Select
                className="profile-select"
                handleInputChange={this.handleInputChange}
                name="lookingForGender"
                placeholder="Select"
                value={this.state.lookingForGender}
                labels={[
                  'males',
                  'females',
                  'nonbinary people',
                  'males & females',
                  'males & nonbinary people',
                  'females & nonbinary people',
                  'people of any gender'
                ]}
                values={[
                  {'Male': true,'Female': false, 'Nonbinary': false},
                  {'Male': false,'Female': true, 'Nonbinary': false},
                  {'Male': false,'Female': false, 'Nonbinary': true},
                  {'Male': true,'Female': true, 'Nonbinary': false},
                  {'Male': true,'Female': false, 'Nonbinary': true},
                  {'Male': false,'Female': true, 'Nonbinary': true},
                  {'Male': true,'Female': true, 'Nonbinary': true},
                ]}
              />
            </div>
            <div className="bottom-elements">
              <div className="question-header">GENDER PREFERENCES</div>
              <Radios
                handleClick={this.handleRadioChange}
                labels={[
                  'I identify as male',
                  'I identify as female',
                  'I identify as nonbinary',
                ]}
                name="gender"
                responses={['Male', 'Female', 'Nonbinary']}
                value={this.state.gender}
              />
            </div>
            <div className="bottom-elements">
              <div className="question-header">GENDER PREFERENCES</div>
              <Checkboxes
                handleClick={this.handleCheckboxChange}
                labels={[
                  'I want to be matched with males',
                  'I want to be matched with females',
                  'I want to be match with nonbinary people',
                ]}
                name="lookingForGender"
                responses={['Male', 'Female', 'Nonbinary']}
                values={this.state.lookingForGender}
              />
            </div>
            <div className="bottom-elements">
              <div className="question-header">SIMILAR OR DIFFERENT</div>
              <Radios
                handleClick={this.handleRadioChange}
                labels={[
                  'I want to be matched with similar people to me',
                  'I want to be matched with different people to me',
                ]}
                name="matchType"
                responses={['similar', 'different']}
                value={this.state.matchType}
              />
            </div>
            {(college === 'Harvard' || college === 'MIT') && (
              <div className="bottom-elements">
                <div className="question-header">HARVARD-MIT MATCHES</div>
                <Checkboxes
                  handleClick={this.handleCheckboxChange}
                  labels={['I want to opt in to cross Harvard-MIT matches!']}
                  name="harvardMIT"
                  responses={['Yes']}
                  values={this.state.harvardMIT}
                />
              </div>
            )}
            <div className="bottom-elements">
              <div className="question-header">SHOW IN SEARCH</div>
              <Toggle
                defaultChecked={this.state.show}
                onChange={this.handleToggleChange}
              />
            </div>
          </div>
          <br />
          <div className="save-button">
            <button
              onClick={this.saveProfile}
              style={{
                opacity: filled ? 1 : 0.8,
                margin: '0 auto',
              }}
            >
              Save Changes
            </button>
          </div>
          {!filled && (
            <div className="incomplete-message">
              Please fully complete profile before saving.
            </div>
          )}
          {!changed && (
            <div className="incomplete-message">You have unsaved changes.</div>
          )}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  return {
    email: state.firebase.auth.email,
    profile: state.firebase.profile,
    profile_pic: state.firebase.profile.profile_pic,
    updateProfile: props.firebase.updateProfile,
  };
};

export default compose(
  firebaseConnect(),
  connect(mapStateToProps),
)(PageProfile);
