/** @jsx jsx **/

import { Component } from 'react';
import { jsx, css } from '@emotion/core';

import Header from 'components/Header';
import Loading from 'components/Loading';

const checkInStyle = css`
  font-weight: bold;

  .conf-container {
    margin: 20px auto;
  }

  p {
    margin-bottom: 1em;
  }

  button {
    max-width: 300px;
    margin-right: 1em;
    margin-bottom: 1em;
    padding: 0.1em;
  }

  input {
    max-width: 359px;
    height: 40px;
    border: none;
    background: #f4f2f2;
    font-family: 'Apercu-light';
    display: flex;
    align-items: center;
    letter-spacing: 0.05em;

    color: #545353;
  }
`;

// TODO: delete
const testDateId = {
  datesAvailable: 49,
  day: "12/28",
  place: "Jefe's",
};

export default class ConfirmationCodes extends Component {

  constructor(props) {
    super(props);

    this.state = {
      codes: [],
      loading: true,
    };
  }

  async componentDidMount() {
    const dateId = this.props.dateId || 'date3'; // TODO change to this.props
    const codeSnapshot = await this.props.firebase
      .ref('/codes')
      .orderByChild('date')
      .equalTo(dateId)
      .once('value');
    this.setState({ loading: false, codes: codeSnapshot.val()})
  }

  render() {
    const { day, place } = testDateId; // TODO change to this.props
    const { loading, codes } = this.state;

    return (
      <div className="RestaurantHome" css={checkInStyle}>
        <Header>{day} {place.toUpperCase()} CONFIRMATION CODES</Header>
        <div>
        <b>Confirmation Codes</b>
        {loading ? 
          <Loading type="spin" size={100} /> 
          :
          <table>
            {Object.keys(codes).map(code => 
              <tr>
                <td>{code}</td>
              </tr>
            )}
          </table>   
        }
      </div>
      </div>
    );
  }
}
