import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PageAbout from './PageAbout';

class PageMain extends Component {
  state = { dorm: '' };

  renderDorm = (dorm, top, left, width, height, transform) => {
    return (
      <div
        onMouseOver={e => {
          this.setState({ dorm: dorm });
        }}
        onTouchEnter={e => {
          this.setState({ dorm: dorm });
        }}
        // onMouseOut={(e) => {this.setState({dorm: ""})}}
        className="dorm"
        style={{
          position: 'absolute',
          top: top,
          left: left,
          width: width,
          height: height,
          transform: transform,
          background: this.state.dorm === dorm ? 'none' : 'none',
        }}
      ></div>
    );
  };

  renderTagline = () => {
    const { dorm } = this.state;
    const taglineMetadata = {
      Mower: {
        tagline: 'At least you’re more known than Lionel',
        position: ['8.5%', '14.5%'],
      },
      Lionel: { tagline: 'What?', position: ['18.3%', '14%'] },
      Stoughton: { tagline: 'See Hollis', position: ['12.4%', '22%'] },
      Hollis: { tagline: 'See Stoughton', position: ['19.8%', '21.5%'] },
      Holworthy: {
        tagline: 'Be your best Hoeworthy self',
        position: ['6.6%', '29%'],
      },
      Thayer: {
        tagline: 'Ding dong, it’s Memorial Church bitch',
        position: ['20%', '35%'],
      },
      Straus: {
        tagline: 'Too close to CVS for bodily health',
        position: ['42.7%', '9%'],
      },
      Matthews: {
        tagline: 'They almost named me Kevins',
        position: ['44%', '18%'],
      },
      'Mass Hall': { tagline: 'Livin’ like Larry', position: ['37%', '15.6%'] },
      Grays: { tagline: 'The Harvard of Harvard', position: ['55%', '20.5%'] },
      Weld: {
        tagline: 'Tiny ass rooms, but at least you have JFK',
        position: ['48%', '28.6%'],
      },
      Wigglesworth: {
        tagline: '"Wiggle wiggle wiggle" — Jason Derulo',
        position: ['71.6%', '33.8%'],
      },
      'Apley Court': {
        tagline: 'We’re lonely, but in our marble bathtubs',
        position: ['82%', '19.8%'],
      },
      Greenough: { tagline: 'More like GreenYES', position: ['73%', '79%'] },
      Hurlbut: {
        tagline: 'In between upper butt and lower butt',
        position: ['89.6%', '70.8%'],
      },
      Pennypacker: {
        tagline: 'Once you go Pack ya never go back',
        position: ['90.6%', '83.8%'],
      },
      Canaday: {
        tagline: 'I love living in a riot shelter',
        position: ['12.7%', '44.5%'],
      },
    };
    return dorm ? (
      <div
        // onMouseOver={(e) => {e.preventDefault();}}
        // onTouchEnter={(e) => {e.preventDefault(); }}
        // onMouseOut={(e) => {e.preventDefault(); }}
        className="tagline"
        style={{
          position: 'absolute',
          top: taglineMetadata[dorm].position[0],
          left: taglineMetadata[dorm].position[1],
          marginLeft: '-3%',
          textAlign: 'center',
          verticalAlign: 'middle',
          width: '15%',
          minWidth: 100,
          fontSize: '1em',
          lineHeight: '1.2em',
        }}
      >
        {taglineMetadata[dorm].tagline}
      </div>
    ) : (
      <div></div>
    );
  };

  render() {
    return (
      <div style={{ height: '100%', textAlign: 'center' }}>
        <div
          style={{
            position: 'relative',
            display: 'flex',
            alignItems: 'center',
            textAlign: 'center',
          }}
        >
          {/*<div style={{width: "11.2%", position: "absolute", top: "52.0%", left: "37.9%", transform: "rotate(11deg)"}}>
              <Logo to="/register"/>
            </div>*/}
          {this.renderDorm(
            'Mower',
            '7.7%',
            '18.5%',
            '2%',
            '7.7%',
            'rotate(14deg)',
          )}
          {this.renderDorm(
            'Lionel',
            '16.3%',
            '17%',
            '2%',
            '7.7%',
            'rotate(14deg)',
          )}
          {this.renderDorm(
            'Stoughton',
            '10.4%',
            '24%',
            '4%',
            '7.7%',
            'rotate(14deg)',
          )}
          {this.renderDorm(
            'Hollis',
            '18.3%',
            '22.5%',
            '4%',
            '6.7%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Holworthy',
            '8.6%',
            '29.0%',
            '8.5%',
            '3.8%',
            'rotate(13deg)',
          )}
          {this.renderDorm(
            'Thayer',
            '15.6%',
            '38.0%',
            '3.5%',
            '14.8%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Straus',
            '40.7%',
            '13.0%',
            '3%',
            '11.7%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Matthews',
            '42.0%',
            '19.0%',
            '3.2%',
            '11.7%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Mass Hall',
            '37.0%',
            '15.6%',
            '6.0%',
            '3.7%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Grays',
            '55.0%',
            '20.6%',
            '8.0%',
            '3.7%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Weld',
            '46.0%',
            '32.6%',
            '3.8%',
            '9.2%',
            'rotate(12deg)',
          )}

          {this.renderDorm(
            'Wigglesworth',
            '67.6%',
            '25.8%',
            '3.6%',
            '2.2%',
            'rotate(18deg)',
          )}
          {this.renderDorm(
            'Wigglesworth',
            '71.0%',
            '30.8%',
            '8.6%',
            '2.5%',
            'rotate(19deg)',
          )}
          {this.renderDorm(
            'Wigglesworth',
            '76.0%',
            '40.8%',
            '8.6%',
            '2.5%',
            'rotate(19deg)',
          )}

          {this.renderDorm(
            'Apley Court',
            '82.0%',
            '22.8%',
            '3.6%',
            '7.0%',
            'rotate(19deg)',
          )}
          {this.renderDorm(
            'Greenough',
            '70.0%',
            '81.0%',
            '3.0%',
            '10.5%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Hurlbut',
            '92.6%',
            '77.8%',
            '5.0%',
            '3.5%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Pennypacker',
            '92.6%',
            '82.8%',
            '4.6%',
            '6.0%',
            'rotate(12deg)',
          )}

          {this.renderDorm(
            'Canaday',
            '12.7%',
            '44.8%',
            '2.0%',
            '6.0%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Canaday',
            '17.5%',
            '45.0%',
            '4.2%',
            '2.0%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Canaday',
            '11.2%',
            '47.2%',
            '7.6%',
            '2.4%',
            'rotate(11deg)',
          )}
          {this.renderDorm(
            'Canaday',
            '15.2%',
            '52.3%',
            '2.0%',
            '6.0%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Canaday',
            '20.2%',
            '52.0%',
            '4.3%',
            '2.2%',
            'rotate(12deg)',
          )}
          {this.renderDorm(
            'Canaday',
            '20.4%',
            '56.5%',
            '4.3%',
            '2.2%',
            'rotate(12deg)',
          )}
          {this.renderTagline()}
          <img alt="yard" src={require('assets/yard.jpg')} style={{ width: '100%' }} />
        </div>
        <div style={{}}>
          <br />
          <PageAbout short />
          <br />
          <br />
          <br />
        </div>
        <div
          className="Links"
          style={{
            position: 'fixed',
            bottom: 0,
            textAlign: 'center',
            width: '100%',
            background: 'white',
            boxShadow: '0 2px 4px 0 rgba(14,30,37,.12)',
          }}
        >
          <div style={{ width: '100%', padding: 30 }}>
            <Link to="/register" className="StartButton">
              Get Started →
            </Link>
          </div>
        </div>
      </div>
    );
  }
}

export default PageMain;
