import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firebaseConnect } from 'react-redux-firebase';

class PageMatch extends Component {
  state = { message: '' };

  render() {
    const matchStr = this.props.match;
    let match = this.props.matches[matchStr];

    if (!match) {
      return <div>loading match...</div>;
    }

    let user = match.user1.id === this.props.uid ? match.user2 : match.user1;

    let day, code, place;
    if (this.props.date) {
      const { date } = this.props;
      day = date.day.toUpperCase();
      code = date.codes[matchStr].toUpperCase();
      place = date.place.toUpperCase();
    }

    return (
      <div className="PageMatch">
        <div className="header">Schedule your meetup!</div>
        <Link to="/app/results">back</Link>

        <br />
        <br />

        {this.props.date && (
          <div>
            <p
              style={{
                color: '#4F5262',
                background: '#F9A9A5',
                border: '2px solid #4F5262',
                boxSizing: 'border-box',
                width: '180px',
                fontWeight: '500',
                fontSize: '16px',
                lineHeight: '20px',
                letterSpacing: '0.145em',
                padding: '5px',
              }}
            >
              {place} {day}
            </p>
            <p
              style={{
                color: '#4F5262',
                fontWeight: 'bold',
                fontSize: '1.2rem',
                textDecoration: 'underline',
              }}
            >
              CODE: {code}
            </p>
            <br />
          </div>
        )}

        <div style={{ background: '#F2F2F2', maxWidth: 820, margin: '0 auto' }}>
          <div style={{ display: 'flex', padding: 20, maxWidth: 600 }}>
            <img
              alt="user profile pic"
              src={user.profile_pic || require('assets/empty.png')}
              style={{
                width: 125,
                height: 125,
                objectFit: 'cover',
                minWidth: 125,
              }}
            />
            <div className={'info'}>
              <div className={'dorm'}>{user.dorm.toUpperCase()}</div>
              <div className="name">{user.name}</div>
              <div className="description">{user.description}</div>
              {/*<div>{match.rating}</div>*/}
            </div>
            <br />
            <br />
          </div>
        </div>
        <br />
        <div
          style={{
            background: '#F2F2F2',
            maxWidth: 820,
            margin: '0 auto',
            padding: 20,
            textAlign: 'center',
            marginBottom: -8,
          }}
        >
          You and {user.name} have a{' '}
          <span style={{ fontFamily: 'apercu' }}>
            {Math.round(match.rating)}%
          </span>{' '}
          compatability rating! Drag over your free times in the scheduling
          tool, choose a convenient location in the poll, and use the chat for
          further coordination! A red dot indicates an unread chat message!
        </div>
        <br />
      </div>
    );
  }
}

const mapStateToProps = (state, props) => {
  const match = props.matches[props.match];
  if (match.date) {
    return {
      date: state.firebase.data.date,
    };
  } else {
    return {};
  }
};

export default compose(
  firebaseConnect(props => [
    {
      path: '/dates/' + props.matches[props.match].date,
      storeAs: 'date',
    },
  ]),
  connect(mapStateToProps),
)(PageMatch);
