/** @jsx jsx **/

import { Component } from 'react';
import { jsx, css } from '@emotion/core';
import { withRouter } from 'react-router-dom';

import Header from 'components/Header';

const checkInStyle = css`
  // font-weight: bold;

  .conf-container {
    margin: 20px auto;
  }

  p {
    margin-bottom: 1em;
  }

  button {
    max-width: 170px;
    margin-right: 1em;
    margin-bottom: 1em;
    padding: 0.1em;
    height: 25px;
  }

  input {
    max-width: 359px;
    height: 40px;
    border: none;
    background: #f4f2f2;
    font-family: 'Apercu-light';
    display: flex;
    align-items: center;
    letter-spacing: 0.05em;

    color: #545353;
  }
`;

const dummySchedule = [
  {
    date: '2/14 (Sat)',
    allotted: 75,
    claimed: 30,
  },
  {
    date: '2/15 (Sun)',
    allotted: 60,
    claimed: 30,
  },
  {
    date: '2/16 (Mon)',
    allotted: 35,
    claimed: 20,
  },
];

class Home extends Component {
  renderDateSchedule() {
    const dateSchedule = this.props.dateSchedule || dummySchedule;
    return (
      <div>
        <b>Date schedule:</b>
        <table>
          <tbody>
            <tr>
              <th>
                <b>Date</b>
              </th>
              <th>
                <b>Claimed</b>
              </th>
              <th>
                <b>Allotted</b>
              </th>
            </tr>
            {dateSchedule.map((date, index) => (
              <tr key={index}>
                <td>{date.date}</td>
                <td>{date.claimed}</td>
                <td>{date.allotted}</td>
              </tr>
            ))}
            <tr>
              <td>
                <b>Total</b>
              </td>
              <td>
                <b>
                  {dateSchedule.reduce((total, num) => total + num.claimed, 0)}
                </b>
              </td>
              <td>
                <b>
                  {dateSchedule.reduce((total, num) => total + num.allotted, 0)}
                </b>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }

  renderButtons() {
    return (
      <div>
        <button onClick={() => this.props.history.push('/restaurant/check-in')}>
          CHECK-IN ONLINE
        </button>
        <button
          onClick={() =>
            this.props.history.push('/restaurant/confirmation-codes')
          }
        >
          PRINT TODAY'S CODES
        </button>
      </div>
    );
  }

  renderInfo() {
    const {
      contractDescription,
      dateDescription,
    } = this.props.restaurantInformation;

    return (
      <div>
        <p>
          <b>Date description: </b>
          {dateDescription || <div>Placeholder description.</div>}
        </p>
        <p>
          <b>Contract description: </b>
          {contractDescription || (
            <div>
              No contract description found! Contact{' '}
              <a href="mailto:cupids@datamatch.me">cupids@datamatch.me</a>.
            </div>
          )}
        </p>
        {this.renderDateSchedule()}
      </div>
    );
  }

  render() {
    return (
      <div className="RestaurantHome" css={checkInStyle}>
        <Header>RESTAURANT PORTAL</Header>
        <p>
          <b>Welcome to the restaurant portal!</b>
          <br />
          You can print confirmation codes below, or see the agreement you
          negotiated with the Datamatch team in more detail.
          <br />
          Contact us at{' '}
          <a href="mailto:cupids@datamatch.me">cupids@datamatch.me</a> if youâ€™d
          like to make changes.
        </p>
        {this.renderButtons()}
        {this.renderInfo()}
      </div>
    );
  }
}

export default withRouter(Home);
