import React, { Component } from 'react';
import { deepCompare } from 'utils/helpers';

import Input from './components/Input';
import Textarea from './components/Textarea';
import Select from './components/Select';
import { Header, PicUpload } from 'components';

export default class PageProfileLayout extends Component {
  constructor(props) {
    super(props);

    this.state = {
      ...this.props.profile,
      show: false,
    };
  }

  handleInputChange = event => {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  };

  render() {
    var equal = deepCompare(this.state, {
      ...this.props.profile,
      show: this.state.show,
    });
    var filled = this.state.name !== '' && !equal;
    return (
      <div className="PageProfile">
        <Header>PROFILE</Header>
        <br />
        {/* <Input text="email" name="email" type="email" value={this.state.email}/> */}
        <div style={{ maxWidth: 600 }}>
          <div style={{ display: 'flex', flexWrap: 'wrap' }}>
            <div style={{ margin: '20px auto' }}>
              <PicUpload />
            </div>
            <br />
            <br />
            <div
              style={{
                display: 'flex',
                flexDirection: 'column',
                flexGrow: 1,
                minWidth: 300,
                // marginTop: 20,
                padding: 20,
              }}
            >
              <Input
                text="Your name"
                name="name"
                type="text"
                value={this.state.name}
                placeholder="Your name"
                handleInputChange={this.handleInputChange}
              />
              <div style={{ height: 15 }} />
              {/*<Input
              text="Last name"
              name="last"
              type="text"
              value={this.state.last}
              placeholder="Last name"
              handleInputChange={this.handleInputChange}
            />
            <br />*/}
              <Select
                text="Dorm"
                name="dorm"
                type="text"
                value={this.state.dorm}
                values={this.props.dorms}
                placeholder="Last name"
                handleInputChange={this.handleInputChange}
              />
              <div style={{ height: 15 }} />
              <Textarea
                text="Description"
                name="description"
                type="text"
                value={this.state.description}
                placeholder="A bit about you"
                handleInputChange={this.handleInputChange}
              />
            </div>
          </div>
          <br />
          <div style={{ margin: '0 auto', textAlign: 'center' }}>
            <button
              onClick={() => {
                this.props.updateProfile({ ...this.state });
                alert('Info saved!');
              }}
              style={{
                opacity: filled ? 1 : 0.8,
                pointerEvents: filled ? 'auto' : 'none',
                margin: '0 auto',
              }}
            >
              Save Changes
            </button>
          </div>
          <br />
        </div>
      </div>
    );
  }
}
